actions.script:
// Get local player's name and score
const pname = runtime.globalVars.playerName;
const pscore = runtime.globalVars.playerScore;

// Using the player's name, fetch its online high-score on the database
const highScore = runtime.objects.JSON.getFirstInstance().getJsonDataCopy()[pname];

// Set the database entry (next action) to the biggest score between the local score and online high-score.
if (pscore < highScore) runtime.globalVars.playerScore = highScore;
